function fnFa64($icon)
{
	$icon = trim($icon);
	$icon = preg_replace('/\s+/', ' ', $icon);

	$ret = $icon;
	switch ($icon) {
		case "text-danger fal fa-arrow-down":
			$ret = "data:image/png;base64,";
			break;
		case "text-info fal fa-arrow-up":
			$ret = "data:image/png;base64,";
			break;
		case "fal fa-birthday-cake fa-4x":
			$ret = "data:image/png;base64,";
			break;
		case "fal fa-flag fa-3x":
