			$ret = "data:image/png;base64,";
			break;
		case "fal fa-sync fa-3x":
			$ret = "data:image/png;base64,";
			break;
		case "fal fa-shopping-cart fa-3x":
			$ret = "data:image/png;base64,";
			break;
	}

	return $ret;
}
